import { Component, EventEmitter, Output } from '@angular/core';
import { RecipeItemComponent } from './recipe-item/recipe-item.component';
import { Recipe } from '../recipe.model';

@Component({
  selector: 'app-recipe-list',
  standalone: true,
  imports: [
    RecipeItemComponent
  ],
  templateUrl: './recipe-list.component.html',
  styleUrl: './recipe-list.component.css'
})
export class RecipeListComponent {
  @Output() recipeWasSelected = new EventEmitter<Recipe>();
  recipes: Recipe[] = [
    new Recipe("A Test Recipe 1", "A Test Recipe Description", "https://static.toiimg.com/photo/77151731.cms"),
    new Recipe("A Test Recipe 2", "A Test Recipe Description", "data:image/jpeg;base64,"),
    new Recipe("A Test Recipe 3", "A Test Recipe Description", "data:image/jpeg;base64,")
  ];

  onRecipeSelected(recipe: Recipe) {
    this.recipeWasSelected.emit(recipe);

  }

}
